using MyStore_Entity_Week4.Models;

namespace MyStore_Entity_Week4.Data;

public class DbSeeder
{
    public static void Seed(ItemDbContext context)
    {
        // Ensure the database is created
        context.Database.EnsureCreated();

        // Check if the database has already been seeded
        if (context.Items.Any()) return; // Database has been seeded

        // Create sample items
        var items = new List<Item>
        {
            new()
            {
                Name = "Laptop", Description = "High-performance laptop", Price = 999.99m,
                ImageUrl =
                    "data:image/jpeg;base64,"
            },
            new()
            {
                Name = "Smartphone", Description = "Latest model smartphone", Price = 699.99m,
                ImageUrl =
                    "data:image/jpeg;base64,"
            },
            new()
            {
                Name = "Headphones", Description = "Noise-cancelling headphones", Price = 199.99m,
                ImageUrl =
                    "data:image/jpeg;base64,"
            },
            new()
            {
                Name = "Tablet", Description = "10-inch tablet", Price = 299.99m,
                ImageUrl =
                    "data:image/jpeg;base64,"
            },
            new()
            {
                Name = "Smartwatch", Description = "Fitness tracking smartwatch", Price = 149.99m,
                ImageUrl =
                    "data:image/jpeg;base64,"
            }
        };

        // Add the items to the context
        context.Items.AddRange(items);

        // Save changes to the database
        context.SaveChanges();
    }
}